import { CART_DECREMENT, CART_INCREMENT } from "../../constants/cartConstants";


const initialState = { count: 0 };

export const counterReducer = (state = initialState, action) => {
  switch (action.type) {
    case CART_INCREMENT:
      return { count: state.count + 1 };
    case CART_DECREMENT:
      return { count: state.count - 1 };

    default:
      return state;
  };   

};
