import React from "react";
import {Route} from 'react-router-dom'
import SearchBox from '../search/SearchBox'
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../redux/actions/User";
import { LinkContainer } from "react-router-bootstrap";
import { Container, Navbar, Nav, NavDropdown } from "react-bootstrap";
import './style.css'


const Header = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const handleLogout = () => {
    dispatch(logout());    
  };
  return (
    <header className=" sticky-top">
      <Navbar
        variant="outline-success"
        expand="lg"
        collapseOnSelect
        className="header sticky search_hover"
      >
        <Container className="m-auto">
          <LinkContainer to="/" className="justify-content-end flex-grow-1">
            <Navbar.Brand className="link_hover">EasyShop</Navbar.Brand>
          </LinkContainer>
          <Navbar.Toggle
            className=""
            aria-controls="toggle-navbar "
            // className="link_hover"
          />
          <Route
            render={({ history }) => (
              <SearchBox className="search_hover" history={history} />
            )}
          />
          <Navbar.Collapse id="toggle-navbar" variant="primary">
            <Nav className="mx-auto justify-content-end flex-grow-1 ">
              <LinkContainer to="/cart">
                <Nav.Link variant="primary">
                  <i className="fas fa-shopping-cart"></i>{" "}
                  <span className="link_hover">Cart</span>
                </Nav.Link>
              </LinkContainer>
              {userInfo ? (
                <NavDropdown title={userInfo.name} id="Username">
                  <LinkContainer to="/profile">
                    <NavDropdown.Item>Profile</NavDropdown.Item>
                  </LinkContainer>
                  <NavDropdown.Item onClick={handleLogout}>
                    Logout
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <LinkContainer to="/login">
                  <Nav.Link>
                    <i className="fas fa-user"></i>
                    <span className="link_hover"> Sign In</span>
                  </Nav.Link>
                </LinkContainer>
              )}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown title="Admin" id="admin">
                  <LinkContainer to="/admin/users">
                    <NavDropdown.Item>Users</NavDropdown.Item>
                  </LinkContainer>

                  <LinkContainer to="/admin/products">
                    <NavDropdown.Item>Products</NavDropdown.Item>
                  </LinkContainer>

                  <LinkContainer to="/admin/orders">
                    <NavDropdown.Item>Orders</NavDropdown.Item>
                  </LinkContainer>
                </NavDropdown>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};


export default Header;
