import React from "react";
import { Button, Card, Image, ListGroup,Row , Col  } from "react-bootstrap";
import { Link } from "react-router-dom";

import Rating from "../rating/Rating";
import "./styles.css";

const Product = ({ product, addToCartHandler }) => {
  return (
    <>
      <Card className="my-3 p-3 rounded " style={{ background: "#eee" }}>
        <Link to={`/Product/${product._id}`}>
          <Card.Img src={product.image} variant="top" />
        </Link>
        <Card.Body>
          <Link to={`/Product/${product._id}`}>
            <Card.Title as="div">
              <strong>{product.name}</strong>
            </Card.Title>
          </Link>
          <Card.Text as="div">
            <Rating
              value={product.rating}
              text={`${product.numReviews} reviews`}
            />
          </Card.Text>
          <Card.Text as="h3" className="d-flex ">
            <div className="my-3 m-auto">${product.price}</div>
          </Card.Text>
        </Card.Body>

        <Row>
            <Col md={6}>
              <Image src={product.image} alt={product.name} fluid />
            </Col>
            <Col md={3}>
              <ListGroup.Item variant="flush">
                <ListGroup.Item>
                  <h4>{product.name}</h4>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Rating
                    value={product.rating}
                    text={`${product.numReviews} reviews`}
                  />
                </ListGroup.Item>
                <ListGroup.Item>Price: ${product.price}</ListGroup.Item>
                <ListGroup.Item>
                  Description: ${product.description}
                </ListGroup.Item>
            </ListGroup.Item>
            </Col>
            </Row>
        <div className="m-auto">
          {product.countInStock === 0 ? (
            <span style={{ color: "red" }}>Out Of Stock</span>
          ) : (
            <Button onclick={addToCartHandler}>view</Button>
          )}
        </div>
      </Card>
    </>
  );
};

export default Product;
