import React, { useState } from "react";
import { Button, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Rating from "../rating/Rating";
import "./styles.css";

const Product = ({ product, history, match }) => {
  const [qty, setQty] = useState(1);
/**/ 
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product: products } = productDetails;
console.log('',productDetails);
  /** */
  console.log(product);
  const addToCartHandler = (productId) => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  /////////////////
  return (
    <>
    <Card className="my-3 p-3 rounded " style={{ background: "#eee" }}>
      <Link to={`/Product/${product._id}`}>
        <Card.Img src={product.image} variant="top" />
      </Link>
      <Card.Body>
        <Link to={`/Product/${product._id}`}>
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>
        <Card.Text as="div">
          <Rating
            value={product.rating}
            text={`${product.numReviews} reviews`}
          />
        </Card.Text>
        <Card.Text as="h3" className="d-flex ">
          <div className="my-3 m-auto">${product.price}</div>
        </Card.Text>
      </Card.Body>
      <Button
        onClick={addToCartHandler}
        variant="light"
        className="my-2 btnUpdate"
        type="button"
        disabled={product.countInStock === 0}
      >
        Add to Cart
      </Button>
      </Card>
      </>
      ////////////////////////////////////////////////////////////////////////////////

  );
};

export default Product;
