/* eslint-disable default-case */
import {
  MY_ORDER_FAIL,
  MY_ORDER_REQUEST,
  MY_ORDER_RESET,
  MY_ORDER_SUCCESS,
  ORDERS_DELIVER_FAIL,
  ORDERS_DELIVER_REQUEST,
  ORDERS_DELIVER_RESET,
  ORDERS_DELIVER_SUCCESS,
  ORDERS_FAIL,
  ORDERS_REQUEST,
  ORDERS_RESET,
  ORDERS_SUCCESS,
  ORDER_CREATE_FAIL,
  ORDER_CREATE_REQUEST,
  ORDER_CREATE_RESET,
  ORDER_CREATE_SUCCESS,
  ORDER_DETAILS_FAIL,
  ORDER_DETAILS_REQUEST,
  ORDER_DETAILS_SUCCESS,
  ORDER_PAY_FAIL,
  ORDER_PAY_REQUEST,
  ORDER_PAY_RESET,
  ORDER_PAY_SUCCESS,
} from "../../constants/orderConstants";

export const orderCreateReducer = (state = {}, action) => {
  switch (action.type) {
    case ORDER_CREATE_REQUEST:
      return { loading: true };
    case ORDER_CREATE_SUCCESS:
      return {
        loading: true,
        success: true,
        order: action.payload,
      };

    case ORDER_CREATE_FAIL:
      return { loading: false, error: action.payload };
    case ORDER_CREATE_RESET:
      return {};
    default:
      return state;
  }
};

export const orderDetailsReducer = (
  state = { loading: true, order: [], shippingAddress: {} },
  action
) => {
  switch (action.type) {
    case ORDER_DETAILS_REQUEST:
      return { ...state, loading: true };
    case ORDER_DETAILS_SUCCESS:
      return {
        loading: false,
        order: action.payload,
      };

    case ORDER_DETAILS_FAIL:
      return { loading: false, error: action.payload };

    default:
      return state;
  }
};
export const orderPayReducer = (state = {}, action) => {
  switch (action.type) {
    case ORDER_PAY_REQUEST:
      return { loading: true };
    case ORDER_PAY_SUCCESS:
      return {
        loading: false,
        success: true,
      };
    case ORDER_PAY_FAIL:
      return { loading: false, error: action.payload };
    case ORDER_PAY_RESET:
      return {};
    default:
      return state;
  }
};
export const myOrderReducer = (state = { orders: [] }, action) => {
  switch (action.type) {
    case MY_ORDER_REQUEST:
      return { loading: true };
    case MY_ORDER_SUCCESS:
      return {
        loading: false,
        orders: action.payload,
      };
    case MY_ORDER_FAIL:
      return { loading: false, error: action.payload };
    case MY_ORDER_RESET:
      return { orders: [] };
    default:
      return state;
  }
};

export const ordersReducer = (state = { orders: [] }, action) => {
  switch (action.type) {
    case ORDERS_REQUEST:
      return { loading: true };
    case ORDERS_SUCCESS:
      return {
        loading: false,
        orders: action.payload,
      };
    case ORDERS_FAIL:
      return { loading: false, error: action.payload };
    case ORDERS_RESET:
      return { orders: [] };
    default:
      return state;
  }
};

export const orderDeliverReducer = (state = {}, action) => {
  switch (action.type) {
    case ORDERS_DELIVER_REQUEST:
      return { loading: true };
    case ORDERS_DELIVER_SUCCESS:
      return {
        loading: false,
        success: true,
      };
    case ORDERS_DELIVER_FAIL:
      return { loading: false, error: action.payload };
    case ORDERS_DELIVER_RESET:
      return {};
    default:
      return state;
  }
};
