import React, {useEffect} from "react";
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { addToCart, removeFromCart } from "../../redux/actions/Cart";
import Rating from "../rating/Rating";
import "./styles.css";

const Product = ({ product, match }) => {
  const productId = match.params.id;
  const dispatch = useDispatch();
   const qty = location.search ? Number(location.search.split("=")[1]) : 1;
 useEffect(() => {
   if (productId) {
     dispatch(addToCart(productId, qty));
   }
 }, [dispatch, productId, qty]);
  const handleAddToCart = () => {
    console.log('adding to cart')
  }


  return (
    <Card
      className="my-3 p-3 rounded card_product"
      style={{ background: "#eee" }}
    >
      <Link to={`/Product/${product._id}`}>
        <Card.Img src={product.image} variant="top" />
      </Link>
      <Card.Body>
        <Link to={`/Product/${product._id}`}>
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>
        <Card.Text as="div">
          <Rating
            value={product.rating}
            text={`${product.numReviews} reviews`}
          />
        </Card.Text>
        <Card.Text as="h3" className="d-flex ">
          <div className="my-3 m-auto">${product.price}</div>
          <button className="btn mx-auto btn_cart" onClick={handleAddToCart}>
            <i className="fas fa-cart-plus fa-lg"></i>
          </button>
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default Product;
